import '.SortingVisualizer.css';

export default class SortingVisualizer extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            array: [],
        };
    }

    componentDidMount() {
        this.reset.Array();
    }

    resetArray() {
        const array = [];
        for (let i = 0; i  < 100; i++) {
            array.push(randomIntFromInterval(1, 100));
        }
        this.setState({array});
    }

}
